set domainId = ##class(iknowAV.util.main).GetDomainId(domName)	

set p1 = ""	

for
{		
	set p1 = $order(^emarkers(p1))
	quit:(p1 = "")	

	set line = ^emarkers(p1)

	// Check, does a string begin with a "!" (! - comment)		
	set checkComment = $PIECE(line,"!", 1, 1)

	if (checkComment = "") { continue }

	// Obtain a term(entity) value.
	set sValue = $piece(line,"#", 1)	
	set entityValue = $piece(line,"#", 2)

	// Obtain the ID of current entity
	set entityId = ##class(%iKnow.Queries.EntityAPI).GetId(domainId, entityValue)	

	kill result	
	// Obtain a list of sentences in the sources containing this entity
	do ##class(%iKnow.Queries.SentenceAPI).GetByEntityIds(.result, domainId, $lb(entityId),, 1, 0)	

	if '($data(result(1))) { continue }			

	set p2 = ""

	// Loop on the sentences contained this entity
	for 
	{					
		set p2 = $order(result(p2))	
		quit:(p2 = "")

		// Obtain the Sentence ID		
		set sentenceId = $list(result(p2), 3)	

		set sourceId = ##class(%iKnow.Queries.SentenceAPI).GetSourceId(domainId, sentenceId)

		// Obtain Sentence ID in the table
		&sql(SELECT ID INTO :id FROM  iknowAV_Hotels_Processing.Sentence WHERE SentenceId = :sentenceId AND DomainName = :domName)

		// If the query does not return anything, then add a new record
		if (SQLCODE = 100) {	
			&sql(INSERT INTO iknowAV_Hotels_Processing.Sentence (DomainName, SentenceId, SrcId) 
				 VALUES (:domName, :sentenceId, :sourceId))
			set id = ^iknowAV.Hotels.PrE05F.SentenceD
		}
		elseif (SQLCODE < 0) {
			write "Error during SQL query.", !
			write "   query returned SQLCODE: ", SQLCODE, !
			return $$$ERROR("SQL query error")	
		}

		// Adding value in the appropriate field. (Positive or Negative) 					
		if (sValue = "+1")		{set $list(^iknowAV.Hotels.PrE05F.SentenceD(id),4) = $list(^iknowAV.Hotels.PrE05F.SentenceD(id),4) + 1 }
		elseif (sValue = "-1")	{set $list(^iknowAV.Hotels.PrE05F.SentenceD(id),5) = $list(^iknowAV.Hotels.PrE05F.SentenceD(id),5) + 1 }

	}	// for (Sentence)

} // while 'stream.AtEnd

return $$$OK

set domainId = ##class(iknowAV.util.main).GetDomainId(domName)	

set length = ##class(%iKnow.Queries.SourceAPI).GetCountByDomain(domainId)

for i=1:1:length {

	set srcId = i

	// Get the list of attribute values of sentences.
	do ##class(%iKnow.Queries.SourceAPI).GetAttributes(.att, domainId, srcId, 1, 0, "", $$$IKATTLVLSENT)

    set j = 1

    // Loop through the list of attribute values ​​to determine the Negation.
    while $data(att(j)) {
    	if ($listget(att(j), 1) = 1) {

			set sentenceId = $listget(att(j),4)

			// Obtain Sentence ID in the table
			&sql(SELECT ID INTO :id FROM  iknowAV_Hotels_Processing.Sentence WHERE SentenceId = :sentenceId AND DomainName = :domName)

			// Switch the Positive and Negative values.
           	if (SQLCODE = 0) {
            	set temp = $list(^iknowAV.Hotels.PrE05F.SentenceD(id), 4)
            	set $list(^iknowAV.Hotels.PrE05F.SentenceD(id),4) = $list(^iknowAV.Hotels.PrE05F.SentenceD(id),5)
            	set $list(^iknowAV.Hotels.PrE05F.SentenceD(id),5) = temp
            	set $list(^iknowAV.Hotels.PrE05F.SentenceD(id),6) = -1
           	}
        }

    	set j = j + 1

    } // while
}    
return $$$OK

set domainId = ##class(iknowAV.util.main).GetDomainId(domName)	

set length = ##class(%iKnow.Queries.SourceAPI).GetCountByDomain(domainId)

// The functional marker's category.
set markerCategory = ""

for i=1:1:length {

	set srcId = i

	// Positive and negative values ​​within the category.
	set ^review(srcId,"positive")  = 0
	set ^review(srcId,"negative")  = 0

	set ^review(srcId,"positiveMarkersSum") = 0
	set ^review(srcId,"negativeMarkersSum") = 0

	// Appraisal within the category.
	set ^review(srcId,"grade") = 0

	// Counter of evaluated categories.
	set ^review(srcId,"gradedCategoryCount") = 0

	// General grade.
	set ^review(srcId,"totalGrade") = 0

	// Positive and negative values ​​in the entire review.
	set ^review(srcId,"totalNegative") = 0
	set ^review(srcId,"totalPositive") = 0
}


set p1 = ""

for
{	
	set p1 = $order(^fmarkers(p1))
	quit:(p1 = "")

	set line = ^fmarkers(p1)

	// Check, does a string begin with a "!" (! - comment)			
	set checkComment = $piece(line,"!",1,1)
	if (checkComment="") { continue }

	set stype = $piece(line,"#",1)

	if (stype = "d") { continue }
	// Processing the string with the name of the category
	elseif (stype = "i") {

		for i=1:1:length {
			set srcId = i
			kill ^review(srcId, "grade")
		}

		// If we have a category (the first pass it does not exist), estimate the grade for this category.
		if (markerCategory '= "") {			

			for i=1:1:length {

				set srcId = i

				if ((^review(srcId,"negative") = 0) && (^review(srcId,"positive") = 0)) { 
					kill ^review(srcId, "grade")	
				}
				elseif (^review(srcId,"negative") = 0) {
					set ^review(srcId,"grade") = 1 
				}
				elseif (^review(srcId,"positive") = 0) {
					set ^review(srcId,"grade") = 0.2 
				}
				else {	
					set ^review(srcId,"grade") = ^review(srcId,"positive") / (^review(srcId,"positive") + ^review(srcId,"negative"))
				}

				if $data(^review(srcId,"grade")) {	

					// Converting grade in the form of 1 to 5
					set ^review(srcId,"grade") = ^review(srcId,"grade") * 5

					set ^review(srcId,markerCategory) = ^review(srcId,"grade")
					set ^review(srcId,"gradedCategoryCount") = ^review(srcId,"gradedCategoryCount") + 1
					set ^review(srcId,"totalGrade") = ^review(srcId,"totalGrade") + ^review(srcId,"grade")

					set ^review(srcId,"positiveMarkersSum") = ^review(srcId,"positiveMarkersSum") + ^review(srcId,"positive")
					set ^review(srcId,"negativeMarkersSum") = ^review(srcId,"negativeMarkersSum") + ^review(srcId,"negative")

				}
				else {
					set ^review(srcId, markerCategory) = "" 
				}

				// Reset positive and negative (for the next category)
				set ^review(srcId,"positive") = 0
				set ^review(srcId,"negative") = 0
			}
		}

		// Obtain the name of the next category
		set markerCategory = $piece(line,"#",2)
	}

	// Processing the string with the term(entity)
	elseif ((stype="t") || (stype="s")) 
	{
		// $piece(line,"#",2) - current term(entity)
		set entityValue = $piece(line,"#",2)
		set entityId = ##class(%iKnow.Queries.EntityAPI).GetId(domainId, entityValue)	

		kill result

		// Obtain a list of sentences in the sources containing this entity					
		set st = ##class(%iKnow.Queries.SentenceAPI).GetByEntityIds(.result, domainId, $lb(entityId),, 1, 0)			

		set p2 = ""

		if '($data(result(1))) { continue }

		// Loop through the sentences contained this entity
		for 
		{					
			set p2 = $order(result(p2))	
			quit:(p2 = "")

			// Obtain the Sentence ID	
			set sentenceId = $list(result(p2), 3)	

			// Obtain the sentence values from table
			&sql(SELECT Negation, Negative, Positive, SrcId 
				 INTO :negation, :sNegative, :sPositive, :srcId 
				 FROM  iknowAV_Hotels_Processing.Sentence 
				 WHERE SentenceId = :sentenceId AND DomainName = :domName)

			// If the query does not return anything go to next sentence
			if (SQLCODE = 100) { continue }

			// Skipping sentences with the negation, if specified the applyNegation parameter
			if ((applyNegation = 0) && (negation = -1)) { continue } 

			// Accumulate the values in the appropriate category
			set ^review(srcId,"positive") = ^review(srcId,"positive") + sPositive
			set ^review(srcId,"negative") = ^review(srcId,"negative") + sNegative
			set ^review(srcId,"totalNegative") = ^review(srcId,"totalNegative") + sNegative
			set ^review(srcId,"totalPositive") = ^review(srcId,"totalPositive") + sPositive

		}	// for (Sentence)

	}	// elseif (stype="t") 		

} // while ('stream.AtEnd)

for i=1:1:length {

	set srcId = i

	// If we have at least one estimated category, estimate the general grade
	if (^review(srcId,"gradedCategoryCount") > 0) {
		set ^review(srcId,"iKnowRating") = ^review(srcId,"totalGrade") / ^review(srcId,"gradedCategoryCount")

		// Present Emarkers grade in the form of 1 to 5.
		set ^review(srcId,"iKnowEmarkersRating") = $SYSTEM.SQL.CEILING($normalize((^review(srcId,"totalPositive") / (^review(srcId,"totalPositive") + ^review(srcId,"totalNegative"))) * 5 - 0.1, -1) + 1)
	}

	&sql(SELECT hotelId INTO :hotelId FROM iknowAV_Hotels_Stat.Review WHERE SourceId = :srcId)

	set hotelName = ##class(%iKnow.Queries.MetadataAPI).GetValueById(domainId, 4, srcId)

	set numberOfEntities = ##class(%iKnow.Queries.EntityAPI).GetCountBySource(domainId, srcId)
	if (numberOfEntities = 0) { set numberOfEntities = 1 }

	set positiveMarkersSum = ^review(srcId,"positiveMarkersSum")
	set negativeMarkersSum = ^review(srcId,"negativeMarkersSum")

	set emotionalSum = positiveMarkersSum + negativeMarkersSum
	set numberOfSentences = ##class(%iKnow.Queries.SentenceAPI).GetCountBySource(domainId, srcId)

	if $data(^review(srcId,"iKnowRating")) {
		set iKnowRating = ^review(srcId,"iKnowRating")
		set iKnowEmarkersRating = ^review(srcId,"iKnowEmarkersRating")
		set comfort = ^review(srcId,"Comfort")
		set food = ^review(srcId,"Food")
		set hospitality = ^review(srcId,"Hospitality")
		set location = ^review(srcId,"Location")
		set service = ^review(srcId,"Service")
		set territory = ^review(srcId,"Territory")
	}
	else {
		set iKnowRating = ""
		set iKnowEmarkersRating = ""
		set comfort = ""
		set food = ""
		set hospitality = ""
		set location = ""
		set service = ""
		set territory = ""
	}


	&sql(UPDATE iknowAV_Hotels_Stat.Review (iKnowEmarkersRating, iKnowRating, iKnowRatingComfort, iKnowRatingFood, iKnowRatingHospitality,
											iKnowRatingLocation, iKnowRatingService, iKnowRatingTerritory)
		 VALUES (:iKnowEmarkersRating, :iKnowRating, :comfort, :food, :hospitality, :location, :service, :territory) 
		 WHERE SourceId = :srcId)

	&sql(INSERT INTO iknowAV_Hotels_Stat.ReviewStat (DomainName, ReviewId, HotelId, HotelName, NumberOfEntities, 
	     EmotionalSum, NumberOfSentences, PositiveMarkersSum, NegativeMarkersSum)
	     VALUES (:domName, :srcId, :hotelId, :hotelName, :numberOfEntities, :emotionalSum, 
	             :numberOfSentences, :positiveMarkersSum, :negativeMarkersSum))

}

return $$$OK

// Obtain a list of all hotels &sql(SELECT %DLIST(DISTINCT HotelId) INTO :hotelIdList FROM iknowAV_Hotels_Stat.Review)
set length = $listlength(hotelIdList)
// Loop through all hotels for i=1:1:length {
set hotelId = $list(hotelIdList, i) &sql(SELECT HotelName INTO :hotelName FROM iknowAV_Hotels_Stat.ReviewStat WHERE HotelId = :hotelId)
// Obtain the final evaluation of hotel &sql(SELECT AVG(NULLIF(iKnowRating, 0)), AVG(NULLIF(iKnowEmarkersRating,0)), AVG(NULLIF(iKnowRatingComfort,0)), AVG(NULLIF(iKnowRatingFood, 0)), AVG(NULLIF(iKnowRatingHospitality, 0)), AVG(NULLIF(iKnowRatingLocation,0)), AVG(NULLIF(iKnowRatingService,0)), AVG(NULLIF(iKnowRatingTerritory,0)), AVG(NULLIF(AuthorRating,0)), AVG(NULLIF(AuthorRatingComfort,0)), AVG(NULLIF(AuthorRatingFood,0)), AVG(NULLIF(AuthorRatingHospitality,0)), AVG(NULLIF(AuthorRatingLocation,0)), AVG(NULLIF(AuthorRatingService,0)), AVG(NULLIF(AuthorRatingTerritory,0)) INTO :iKnowRating, :iKnowEmarkersRating, :iKnowRatingComfort, :iKnowRatingFood, :iKnowRatingHospitality, :iKnowRatingLocation, :iKnowRatingService, :iKnowRatingTerritory, :authorRating, :authorRatingComfort, :authorRatingFood, :authorRatingHospitality, :authorRatingLocation, :authorRatingService, :authorRatingTerritory FROM iknowAV_Hotels_Stat.Review WHERE HotelId = :hotelId)
set neutralHighBorder = ..#NEUTRALHIGHBORDER set neutralLowBorder = ..#NEUTRALLOWBORDER
&sql(SELECT COUNT(ID) INTO :positiveReviews FROM iknowAV_Hotels_Stat.Review WHERE HotelId = :hotelId AND iKnowRating > :neutralHighBorder) &sql(SELECT COUNT(ID) INTO :negativeReviews FROM iknowAV_Hotels_Stat.Review WHERE HotelId = :hotelId AND iKnowRating < :neutralLowBorder) &sql(SELECT COUNT(ID) INTO :neutralReviews FROM iknowAV_Hotels_Stat.Review WHERE HotelId = :hotelId AND iKnowRating <= :neutralHighBorder AND iKnowRating >= :neutralLowBorder) &sql(SELECT COUNT(ID) INTO :numberOfReviews FROM iknowAV_Hotels_Stat.Review WHERE HotelId = :hotelId AND iKnowRating > 0)
set positiveReviewsPCT = 0 set negativeReviewsPCT = 0 set neutralReviewsPCT = 0
if (numberOfReviews > 0) { set positiveReviewsPCT = positiveReviews / numberOfReviews * 100 set negativeReviewsPCT = negativeReviews / numberOfReviews * 100 set neutralReviewsPCT = neutralReviews / numberOfReviews * 100 }
// Write the results in the table &sql(INSERT OR UPDATE INTO iknowAV_Hotels_Stat.Hotel (HotelId, HotelName, NegativeReviewsPCT, PositiveReviewsPCT, NeutralReviewsPCT, ReviewsCount, UserRating, UserRatingComfort, UserRatingFood, UserRatingHospitality, UserRatingLocation, UserRatingService, UserRatingTerritory, iKnowRating, iKnowEmarkersRating, iKnowRatingComfort, iKnowRatingFood, iKnowRatingHospitality, iKnowRatingLocation, iKnowRatingService, iKnowRatingTerritory) VALUES (:hotelId, :hotelName, :negativeReviewsPCT, :positiveReviewsPCT, :neutralReviewsPCT, :numberOfReviews, :authorRating, :authorRatingComfort, :authorRatingFood, :authorRatingHospitality, :authorRatingLocation, :authorRatingService, :authorRatingTerritory, :iKnowRating, :iKnowEmarkersRating, :iKnowRatingComfort, :iKnowRatingFood, :iKnowRatingHospitality, :iKnowRatingLocation, :iKnowRatingService, :iKnowRatingTerritory)) }
return $$$OK

