&sql(INSERT INTO iknowAV_Hotels_Stat.HotelStat (HotelId, HotelName, PositiveMarkersSum, NegativeMarkersSum, EmotionalSum, NumberOfEntities, NumberOfSentences) SELECT HotelId, HotelName, AVG(PositiveMarkersSum), AVG(NegativeMarkersSum), AVG(EmotionalSum), AVG(NumberOfEntities), AVG(NumberOfSentences) FROM iknowAV_Hotels_Stat.ReviewStat GROUP BY HotelId )
set processingResult = ##class(iknowAV.Hotels.Stat.ProcessingResult).%New() set processingResult.DomainName = domName
// Counting lines in a file of emotional markers #dim filename As %String = ^iknowAVDomainFolder(domName)_"emarkers.txt" #dim stream As %Stream.FileCharacter = ##class(%Stream.FileCharacter).%New() set status = stream.LinkToFile(filename)
set lineCounter = 0
while '(stream.AtEnd) { do stream.ReadLine() set lineCounter = lineCounter + 1 }
set processingResult.EDictionarySize = lineCounter
// Counting lines in a file of functional markers set filename = ^iknowAVDomainFolder(domName)_"fmarkers.txt" set stream = ##class(%Stream.FileCharacter).%New() set status = stream.LinkToFile(filename)
set lineCounter = 0
while '(stream.AtEnd) { do stream.ReadLine() set lineCounter = lineCounter + 1 }
set processingResult.FDictionarySize = lineCounter
// Obtain the average value Emotional Saturation of all hotels &sql(SELECT AVG(EmotionalSaturation), AVG(PositiveEmotionalSaturation), AVG(NegativeEmotionalSaturation) INTO :emotionalSaturation, :positiveEmotionalSaturation, :negativeEmotionalSaturation FROM iknowAV_Hotels_Stat.HotelStat)
set processingResult.EmotionalIndex = emotionalSaturation / processingResult.EDictionarySize * 1000 set processingResult.PositiveIndex = positiveEmotionalSaturation / processingResult.EDictionarySize * 1000 set processingResult.NegativeIndex = negativeEmotionalSaturation / processingResult.EDictionarySize * 1000
#dim numOfSources As %Integer = ##class(%iKnow.Queries.SourceAPI).GetCountByDomain(domId) set processingResult.NumberOfSources = numOfSources set processingResult.Negation = applyNegation set processingResult.ProcessingDateTime = $zdatetime($h,3)
set tSC = processingResult.%Save()
