// Set the filter with HotelId
set filter = ##class(%iKnow.Filters.SimpleMetadataFilter).%New(domainId, "HotelId","=",hotelId)	

// Set the flag to 0
set categoryFound = 0

set p1 = ""

for
{	
	set p1 = $order(^fmarkers(p1))
	quit:(p1 = "")

	set line = ^fmarkers(p1)

	// Check, does a string begin with a "!" (! - comment)	
	set checkComment = $piece(line,"!",1,1)
	if (checkComment="") { continue }

	set stype = $piece(line,"#",1)

	if (stype = "d") { continue }
	// Processing the string with the category name
	elseif (stype = "i") {

		// Obtain the name of the next category
		set markerCategory = $piece(line,"#",2)

		if (categoryName '= markerCategory) { continue }
		else {
			set categoryFound = 1 
		}
	}
	// Processing the string with term(entity)
	elseif (stype="t") {
		if (categoryName '= markerCategory) { continue }

		set currentEntity = $piece(line,"#",2)	
		set entityId = ##class(%iKnow.Queries.EntityAPI).GetId(domainId,currentEntity)

		if (entityId = "") { continue }			

		set $list(entityIdList, *+1) = entityId			
	}
}

if (categoryFound = 0) { 
	return $$$ERROR("There is no choosen category.") 
}

kill srcResult

// Obtain all Sources (filtered by hotel)
do ##class(%iKnow.Queries.SourceAPI).GetByDomain(.srcResult, domainId, 1, 0, filter)

// Obtain the number of Sources (filtered by hotel)
set numberOfReviews = ##class(%iKnow.Queries.SourceAPI).GetCountByDomain(domainId, filter)

for i=1:1:numberOfReviews {
	set $list(srcIdList, *+1) = $list(srcResult(i), 1)	
}

set srcIdFilter = ##class(%iKnow.Filters.SourceIdFilter).%New(domainId, srcIdList)

kill result

set iKnowCategory = "iKnowRating"_categoryName			

// Obtain a list of sentences in the source containing this entity						
do ##class(%iKnow.Queries.SentenceAPI).GetByEntityIds(.result, domainId, entityIdList, srcIdFilter, 1, 0)					

set p2 = ""

// Loop through the sentences contained most spread Entity
for 
{					
	set p2 = $order(result(p2))	
	quit:(p2 = "")

	set sentenceId = $list(result(p2), 3)	

	// Obtain Negation of current sentence
	&sql(SELECT Negation INTO :negation FROM  iknowAV_Hotels_Processing.Sentence WHERE SentenceId = :sentenceId AND DomainName = :domName)

	if (SQLCODE = 100) { continue }
	if (negation = -1) { continue } 

	set currentSentence = ##class(%iKnow.Queries.SentenceAPI).GetValue(domainId, sentenceId)

	set sentenceLength = $length(currentSentence, " ")

	if (sentenceLength < numberOfWordsInSentence) {
		set $list(allResult,*+1) = sentenceId
	}

 }	// for (Sentence)

&sql(SELECT id INTO :hotelObjId FROM iknowAV_Hotels_Stat.Hotel WHERE HotelId = :hotelId)

