return sum / length

set sum = 0
set length = $listlength(valueList)
set mean = ..Mean(valueList)

if (length = 0) { return ""}

for i=1:1:length {
	set sum = sum + $zpower(($list(valueList,i) - mean), 2)
}	

return sum / length


