#dim confName As %String = "Conf"_domName
#dim domId As %Integer = ##class(%iKnow.Domain).GetOrCreateId(domName)
#dim initialNumOfSources As %Integer = ##class(%iKnow.Queries.SourceAPI).GetCountByDomain(domId)

