set tSC = $$$OK, tTitle = ""
try {

	// create filter object (Module 3, exercise 4)
	set tFilter = ""
	if (..Agency'="") {
		set tFilter = ##class(%iKnow.Filters.SimpleMetadataFilter).%New(..Domain, "Agency", "=", ..Agency)
